import React, { useEffect, useState } from "react";
import axios from "axios";
import "../App.css";
import { useGetUserID } from "../hooks/useGetUserID";
import { useParams } from "react-router-dom";
export const SearchResults = () => {
  const userID = useGetUserID();
  const [isLoggedIn,setIsLoggedIn] = useState(!!userID);
  const [cookies] = useCookies(['access_token']);
  const [searchResults, setSearchResults] = useState([]);
const {searchTerm}=useParams();
console.log(searchTerm);
useEffect(() => {
  setIsLoggedIn(!!userID);
  const fetchSearchResults = async () => {
    try {
      console.log(searchTerm);
      const response = await axios.get(`http://localhost:4000/posts/search/${searchTerm}`);
      setSearchResults(response.data);

    } catch (err) {
      console.log(err);
    }
  };

  if (searchTerm) {
    fetchSearchResults();
  }
}, [searchTerm])
const likePost = async (postID) => {
  const postIndex = posts.findIndex(post => post._id === postID);
  if (!posts[postIndex].likes.includes(userID)) {
    try {
      const response = await axios.put('http://localhost:4000/posts/like', {
        postID,
        userID,
      }, {
        headers: {
          'Authorization': `Bearer ${cookies.access_token}`
        },
        withCredentials: true
      });

      const updatedPosts = [...posts];
      updatedPosts[postIndex].likes.push(userID);
      updatedPosts[postIndex].likesCount = response.data.likesCount;
      setSearchResults(updatedPosts);
    } catch (err) {
      console.log(err);
    }
  }
};

const unlikePost = async (postID) => {
  const postIndex = posts.findIndex(post => post._id === postID);
  if (posts[postIndex].likes.includes(userID)) {
    try {
      const response = await axios.put('http://localhost:4000/posts/unlike', {
        postID,
        userID,
      }, {
        headers: {
          'Authorization': `Bearer ${cookies.access_token}`
        },
        withCredentials: true
      });

      const updatedPosts = [...posts];
      const userIndex = updatedPosts[postIndex].likes.indexOf(userID);
      updatedPosts[postIndex].likes.splice(userIndex, 1);
      updatedPosts[postIndex].likesCount = response.data.likesCount;
      setPosts(updatedPosts);
    } catch (err) {
      console.log(err);
    }
  }
};
  return (
    <div className="home-container">
      <h1>Search Results</h1>
      <ul className="list-unstyled">
        {searchResults.length === 0 ? (
          <p>No results matched your search criteria. Please retry or modify your search.</p>
        ) : (
          searchResults.map((post) => (
            <li key={post._id} className="cardu">
            <div className="card-body d-flex justify-content-between align-items-center">
              <div>
                <h2 className="card-title">{post.title}</h2>
              </div>
              
            </div>
            <div className="card-body">
            <img src={post.imageUrl} alt={post.title} className="img-fluid" />
              <div className="instructions">
                <p className="card-text">{post.content}</p>
              </div>
              <div className="tags">
              {
                post.tags.map((tags) => (
                  <ul className="ingredient-list">
                    <li className="card-text">#{tags}</li>
                  </ul>
                ))
              }</div>
             
            </div>
          </li>
          ))
        )}
      </ul>
    </div>
  );
};
