import React, { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";

import "../styles/login.css";

export const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(""); // State for error message

  const [cookies, setCookies] = useCookies(["access_token"]);
  const navigate = useNavigate();

  const isLoggedIn = !!cookies.access_token;

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post(
        "http://localhost:4000/auth/login",
        {
          email,
          password,
        }
      );

      setCookies("access_token", response.data.token);
      window.localStorage.setItem("userID", response.data.userID);
      window.localStorage.setItem("userName", response.data.username);
      navigate("/");
    } catch (error) {
      console.error(error);
      setError("Login failed. Please check your email and password."); // Set error message
    }
  };

  if (isLoggedIn) {
    navigate("/"); // Redirect the user to the home page if already logged in
    return null; // Return null or any loading indicator while redirecting
  }

  return (
    <div className="login container row col-12">
      <div className="form-container col-md-6 mt-5">
        <h1 className="title">Welcome back!</h1>
        <p className="subtitle">Please enter your details</p>
        <form onSubmit={handleSubmit}>
          {/* Rest of the login form */}
        </form>
        {error && <p className="error-message">{error}</p>} {/* Display error message */}
      </div>
    </div>
  );
};
