import React, { createContext, useContext, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import { Home } from './pages/home';
import { Login } from './pages/login';
import { Register } from './pages/register';
import { CreatePost } from './pages/create-post';
import { SavedPosts } from './pages/saved-posts';
import { Navbar } from './components/Navbar' ; 
import { SearchBar } from './SearchBar';
import 'bootstrap/dist/css/bootstrap.min.css';
import { SearchResults } from './pages/SearchResults';
import { MyPosts } from './pages/myposts';
import { EditPost } from './pages/editPost';

const AuthContext = createContext();

function AuthProvider({ children }) {
  const navigate = useNavigate();
  const accessToken = document.cookie.split('; ').find(row => row.startsWith('access_token'));

  useEffect(() => {
    if (!accessToken) {
      navigate('/login');
    }
  }, [accessToken, navigate]);

  return (
    <AuthContext.Provider value={!!accessToken}>
      {children}
    </AuthContext.Provider>
  );
}

function ProtectedRoute({ path, element }) {
  const isAuthenticated = useContext(AuthContext);

  if (!isAuthenticated) {
    return null;
  }

  return <Route path={path} element={element} />;
}

function App() {
  return (
    <div className="App">
      <Router>
        <AuthProvider>
          <Navbar />
          <SearchBar/>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <ProtectedRoute path="/create-post" element={<CreatePost />} />
            <ProtectedRoute path="/saved-posts" element={<SavedPosts />} />
            <ProtectedRoute path="/myposts" element={<MyPosts />} />
            <ProtectedRoute path="/searchresults/:searchTerm" element={<SearchResults />} />
            <ProtectedRoute path="/editPost/:postId" element={<EditPost/>}/>
          </Routes>
        </AuthProvider>
      </Router>
    </div>
  );
}

export default App;
