import React from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import { useGetUserID } from '../hooks/useGetUserID';
import { useNavigate } from 'react-router-dom';

export const CreatePost = () => {
    const { register, handleSubmit, watch, setValue } = useForm();
    const userID = useGetUserID();
    const navigate = useNavigate();

    const tags = watch('tags', []);

    const onSubmit = async (data) => {
        data.userOwner = userID;
        console.log(data);
        try {
            await axios.post('http://localhost:4000/posts', data);
            alert('Post uploaded!');
            navigate('/');
        } catch (error) {
            console.error(error);
        }
    };

    const handleAddTag = () => {
        setValue('tags', [...tags, '']);
    };

    const handleRemoveTag = (index) => {
        setValue('tags', tags.filter((tag, i) => i !== index));
    };

    return (
        <div className='create-recipe'>
            <h2>New Post</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
                <div className='form-group'>
                    <label htmlFor='title'>Title</label>
                    <input
                        type='text'
                        id='title'
                        name='title'
                        {...register('title')}
                        className='form-control'
                    />
                </div>

                     <div className="form-group">
        <label htmlFor="tags">Tags</label>  <button type="button" onClick={handleAddTag} className="btn btn-secondary mb-3 add-tag-button">+</button>
        {tags.map((tag, index) => (
          <div key={index} className="input-group mb-2">
            <input
              type="text"
              name="tags"
              value={tag}
              onChange={(event) => handleAddTag(event, index)}
              className="form-control"
            />
            <div className="input-group-append">
              <button type="button" className="btn btn-danger" onClick={() => handleRemoveTag(index)}>-</button>
            </div>
          </div>
        ))}
      
      </div>
                <div className='form-group'>
                    <label htmlFor='content'>Content</label>
                    <textarea
                        id='content'
                        name='content'
                        {...register('content')}
                        className='form-control'
                    />
                </div>

                <div className='form-group'>
                    <label htmlFor='imageUrl'>Image URL</label>
                    <input
                        type='text'
                        id='imageUrl'
                        name='imageUrl'
                        {...register('imageUrl')}
                        className='form-control'
                    />
                </div>

                <button type='submit' className='btn btn-primary'>
                    Upload
                </button>
            </form>
        </div>
    );
};
