import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Home } from './pages/home';
import { Login } from './pages/login';
import { Register } from './pages/register';
import { CreatePost } from './pages/create-post';
import { SavedPosts } from './pages/saved-posts';
import { Navbar } from './components/Navbar' ; 
import { SearchBar } from './SearchBar';
import 'bootstrap/dist/css/bootstrap.min.css';
import { SearchResults } from './pages/SearchResults';
import { MyPosts } from './pages/myposts';
import { EditPost } from './pages/editPost';
import { Navigate, useLocation } from 'react-router-dom';
function App() {
  function ProtectedRoute({ element, path }) {
    const userID = sessionStorage.getItem('userID');
  
    if (!userID) {
      return <Navigate to="/login" replace />;
    }
  
    return <Route path={path} element={element} />;
  }
  return (
    <div className="App">
     <Router>
  <Navbar />
  <SearchBar/>
  <Routes>
    <Route path="/" element={<Home />} />
    <Route path="/login" element={<Login />} />
    <Route path="/register" element={<Register />} />
    <ProtectedRoute path="/create-post" element={<CreatePost />} />
    <ProtectedRoute path="/saved-posts" element={<SavedPosts />} />
    <ProtectedRoute path="/myposts" element={<MyPosts />} />
    <ProtectedRoute path="/searchresults/:searchTerm" element={<SearchResults />} />
    <ProtectedRoute path="/editPost/:postId" element={<EditPost/>}/>
  </Routes>
</Router>
    </div>
  );
}

export default App;
